import "../styles/list.css";
import { React, useState, useEffect } from "react";
// import React, { useState, useEffect, useRef } from "react";

const [allMenu, allMenuSet] = useState([]);
export const detaSort = function () {
  allMenuSet(allMenu.map((category) => category.category === "ごはん"));
};

export const List = (props) => {
  const URL =
    process.env.NODE_ENV === "production"
      ? //■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■
        "https://choose-lunch-app.onrender.com"
      : //■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■ここのリンクを変更すること■■■■■■■■■■■■■■■■■■■■■■
        "http://localhost:8080";

  const getDataFunc = () => {
    fetch(`${URL}/table`, { method: "GET" })
      .then((res) => res.json())
      .then((getData) => {
        allMenuSet(getData);
        //allMenuをgetDataとする
        //全てのオブジェクトが入った配列
      });
  };

  // let patchCheck;

  //これを入れないと永遠にループする
  useEffect(() => {
    getDataFunc();
  }, []);

  console.log(process.env);
  return (
    <>
      <div className="list">
        menu lists
        <ul>
          {allMenu.map((menu) => (
            <li key={menu.id}>{menu.menu}</li>
          ))}
        </ul>
      </div>
    </>
  );
};
