import { React, useState } from "react";

export const Search = (props) => {
  //   const riceDataSort = () => {
  //     fetch(`${URL}/table`, { method: "GET" })
  //       .then((res) => res.json())
  //       .then((getData) => {
  //         allMenuSet(getData.filter(item.category === "ごはん")
  // )})
  //   };
  const riceClickAction = () => {
    fetch(
      `${URL}/table/resetIsWaiting`,
      {
        method: "PUT",
      }

        .then((res) => res.json())
        .then((getData) => {
          allMenuSet(getData);
          //allMenuをgetDataとする
          //全てのオブジェクトが入った配列
        })
    );
  };

  return (
    <>
      <button id="rice" onClick={riceClickAction}>
        {" "}
        ごはん
      </button>
      <button>パスタ</button>
    </>
  );
};
