import { React, useState, useEffect, useImperativeHandle } from "react";
// import { allMenu, allMenuSet } from "../App";
// import React, { useState, useEffect, useRef } from "react";
const URL =
  process.env.NODE_ENV === "production"
    ? //■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■
      "https://choose-lunch-app.onrender.com"
    : //■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■ここのリンクを変更すること■■■■■■■■■■■■■■■■■■■■■■
      "http://localhost:8080";

export const List = (props) => {
  const [allMenu, allMenuSet] = useState([]);

  //この関数を実行すると指定したURLにGETリクエストを送る
  const getDataFunc = () => {
    fetch(`${URL}/table`, { method: "GET" })
      .then((res) => res.json())
      .then((getData) => {
        allMenuSet(getData);
        //allMenuをgetDataとする
        //全てのオブジェクトが入った配列
      });
  };

  // let patchCheck;

  //これを入れないと永遠にループする
  useEffect(() => {
    getDataFunc();
  }, []);
  console.log(allMenu);

  const menuView = () => {
    const elementsArr = [];
    //allMenuはオブジェクトが入った配列
    allMenu.forEach((value) => {
      if (value.isWaiting === true) {
        console.log(value);
        elementsArr.push(
          <>
            <div className="list">
              menu lists
              <ul>
                <li key={value.id}>{value.menu}</li>
              </ul>
            </div>
          </>
        );
      }
    });
    return elementsArr;
  };
  const resultElements = menuView();
  return resultElements;
};
