import "../styles/list.css";
import { React, useState, useEffect } from "react";
// import React, { useState, useEffect, useRef } from "react";
const URL =
  process.env.NODE_ENV === "production"
    ? //■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■
      "https://choose-lunch-app.onrender.com"
    : //■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■ここのリンクを変更すること■■■■■■■■■■■■■■■■■■■■■■
      "http://localhost:8080";

export const dataSort = () => {};

export const List = (props) => {
  const [allMenu, allMenuSet] = useState([]);

  //この関数を実行すると指定したURLにGETリクエストを送る
  const getDataFunc = () => {
    fetch(`${URL}/table`, { method: "GET" })
      .then((res) => res.json())
      .then((getData) => {
        allMenuSet(getData);
        //allMenuをgetDataとする
        //全てのオブジェクトが入った配列
      });
  };

  // let patchCheck;

  //これを入れないと永遠にループする
  useEffect(() => {
    getDataFunc();
  }, []);

  const menuView = () => {
    if (allMenu.isWaiting === true) {
      return (
        <>
          <div className="list">
            menu lists
            <ul>
              {allMenu.map((menu) => (
                <li key={menu.id}>{menu.menu}</li>
              ))}
            </ul>
          </div>
        </>
      );
    }
  };
  menuView();
};
